    def parse(self):
        with open(self.file_path, 'r') as file:
            lines = file.readlines()
            for i, line in enumerate(lines):
                stripped_line = line.strip()
                if not stripped_line or stripped_line.startswith('# '):
                    continue
                elements = stripped_line.split(' ')
                assert len(elements) % 2 == 1, f"Uneven number of elements in line {i+1}: {stripped_line}"
                for j in range(0, len(elements), 2):
                    identifier = elements[j].replace('_', '-')
                    node_data = {'identifier': identifier, 'children': []}
                    child_data = {'identifier': elements[j+1].replace('_', '-'), 'children': []}
                    node_data['children'].append(child_data)
                    if i == 0 or elements[j] == self.nodes[i-1].identifier:
                        self.nodes.append(node_data)

    def pretty_print(self):
        root = self.nodes.pop(0)
        self._pretty_print_recursive(root)
        print(json.dumps(root, indent=4))

    def _pretty_print_recursive(self, node):
        queue = deque([node])
        index = 0
        while queue:
            current = queue.popleft()
            if current['children']:
                print(' ' * index * 4, current['identifier'])
                index += 1
                queue.extend(current['children'])
            else:
                print(' ' * index * 4, current['identifier'])

    def count_nodes(self):
        return sum(self._count_nodes_recursive(node) for node in self.nodes)

    def _count_nodes_recursive(self, node):
        if not node['children']:
            return 1
        return 1 + sum(self._count_nodes_recursive(child) for child in node['children'])

    def add_parents(self):
        self._add_parents_recursive(self.nodes, [])

    def _add_parents_recursive(self, nodes, parents):
        current = nodes.pop(0)
        current_copy = deepcopy(current)
        current_copy['parent'] = parents
        if 'children' in current:
            for child in current['children']:
                self._add_parents_recursive(current['children'], current_copy['children'])
        else:
            current.append(current_copy)
            self.nodes.append(current)
        self.nodes.append(self.nodes.pop(0))  # Mimic the behavior of appending at the end based on 'pop' and 'append' operations.

